mapper.insert:
insert into qna_board (
   qna_title, qna_content, qna_userid, qna_datetime
) values (
   #{qnaTitle}, #{qnaContent}, #{qnaUserid}, now()
)

mapper.update:
update qna_board 
   set qna_title = #{qnaTitle}, 
       qna_content = #{qnaContent}, 
       qna_userid = #{qnaUserid}
 where qna_no = #{qnaNo}

update qna_board 
   set reply_datetime = now(), 
       reply_content = #{replyContent}, 
       reply_userid = #{replyUserid}
 where qna_no = #{qnaNo}

update qna_board 
   set reply_datetime = now(), 
       reply_content = null, 
       reply_userid = #{replyUserid}
 where qna_no = #{qnaNo}
